import React, { useContext, useEffect, useState } from "react";

import {
  ItemCompanyProps,
  ListEventsProps,
  ListGuestProps,
} from "../../@types";
import { InfoContext } from "../Contexts/infoContext";
import { api } from "../utils/api.ts";

export const InfoProvider: React.FC<{ children: React.ReactNode }> = ({
  children,
}) => {
  const [timeNow, setTimeNow] = useState<string>("");
  const [selectedEvent, setSelectedEvent] = useState("");
  const [allEvents, setAllEvents] = useState<ListEventsProps[]>([]);
  const [guests, setGuests] = useState<ListGuestProps[]>([]);
  const [perPage, setPerPage] = useState<number>(5);
  const [skip, setSkip] = useState<number>(0);
  const [search, setSearch] = useState<string>("");
  const [searchByEvent, setSearchByEvent] = useState<string>("");
  const [totalGuests, setTotalGuests] = useState<number>(0);

  useEffect(() => {
    if (skip === 0) setSkip(0);
    if (skip > totalGuests) setSkip(totalGuests);
    setInterval(() => {
      const dateFormat = new Intl.DateTimeFormat("pt-BR", {
        year: "2-digit",
        month: "numeric",
        day: "numeric",
        hour: "numeric",
        minute: "numeric",
        second: "numeric",
        hour12: false,
      });

      setTimeNow(dateFormat.format(new Date()));
    }, 1000);

    api.get("/events").then((res) => setAllEvents(res.data.getEvents));

    api.get("/guests", { params: { perPage, skip } }).then((res) => {
      setGuests(res?.data?.guests);
      setTotalGuests(res.data.totalGuests);
    });
  }, [perPage, skip, searchByEvent, search]);

  return (
    <InfoContext.Provider
      value={{
        timeNow,
        setTimeNow,
        allEvents,
        setAllEvents,
        selectedEvent,
        setSelectedEvent,
        search,
        setSearch,
        totalGuests,
        guests,
        setGuests,
        perPage,
        setPerPage,
        skip,
        setSkip,
        searchByEvent,
        setSearchByEvent,
      }}
    >
      {children}
    </InfoContext.Provider>
  );
};

export default InfoProvider;

export function useInfo() {
  const useInfo = useContext(InfoContext);
  return useInfo;
}
