import React, { useContext, useEffect, useState } from "react";

import {
  ItemCompanyProps,
  ListEventsProps,
  ListGuestProps,
} from "../../@types";
import { InfoContext } from "../Contexts/infoContext";
import { api } from "../utils/api.ts";

export const InfoProvider: React.FC<{ children: React.ReactNode }> = ({
  children,
}) => {
  const [timeNow, setTimeNow] = useState<string>("");
  const [selectedEvent, setSelectedEvent] = useState("");
  const [allEvents, setAllEvents] = useState<ListEventsProps[]>([]);
  const [allGuests, setAllGuests] = useState<ListGuestProps[]>([]);
  const [searchGuest, setSearchGuest] = useState<string>("");
  const [completeList, setCompleteList] = useState<ListGuestProps[]>([]);
  const [filteredList, setFilteredList] = useState<ListGuestProps[]>([]);
  const [allCompanies, setAllCompanies] = useState<ItemCompanyProps[]>([]);
  const [totalGuests, setTotalGuests] = useState<number>(0);

  function findGuest() {
    api.get("/guests/").then((res) => {
      const temporaryGuestList: ListGuestProps[] = res.data.getGuests?.map(
        (data: ListGuestProps) => {
          return {
            ...data,
            event: "N/A",
            check: "N/A",
            time: "N/A",
          };
        }
      );

      setAllGuests(temporaryGuestList);
      setTotalGuests(res.data.getGuests.length);
    });
  }

  useEffect(() => {
    setInterval(() => {
      const dateFormat = new Intl.DateTimeFormat("pt-BR", {
        year: "2-digit",
        month: "numeric",
        day: "numeric",
        hour: "numeric",
        minute: "numeric",
        second: "numeric",
        hour12: false,
      });

      setTimeNow(dateFormat.format(new Date()));
    }, 1000);

    api.get("/events").then((res) => setAllEvents(res.data.getEvents));

    api.get("/guests").then((res) => {
      // setAllGuests(res.data.getGuests);
      setTotalGuests(res.data.getGuests.length);
      console.log(res.data.getGuests[0]);
    });
  }, []);

  useEffect(() => {
    // console.log(object);
  }, [searchGuest]);

  // useEffect(() => {

  //   const temporaryCompaniesList: string[] = [];
  //   for (let i = 0; i < temporaryList?.length; i++) {
  //     if (
  //       temporaryCompaniesList?.indexOf(temporaryList[i].companyName) === -1
  //     ) {
  //       temporaryCompaniesList?.push(temporaryList[i].companyName);
  //     }
  //   }

  //   setAllCompanies(
  //     temporaryCompaniesList.map((data) => {
  //       return {
  //         companyName: data,
  //         checkedInGuest: 0,
  //       };
  //     })
  //   );

  //   for (let i = 0; i < temporaryList?.length; i++) {
  //     for (let j = 0; j < allEvents?.length; j++) {
  //       if (temporaryList[i].invitationId === allEvents[j].invitationId) {
  //         temporaryList[i].event = allEvents[j].name;
  //       }
  //     }
  //   }

  //   setCompleteList(temporaryList);
  // }, [dataGuests]);

  useEffect(() => {
    if (selectedEvent === "") {
      setFilteredList([]);
    } else {
      const filteredGuest = completeList.filter(
        (guests) => guests.event === selectedEvent
      );
      setFilteredList(filteredGuest);
    }
  }, [selectedEvent]);

  return (
    <InfoContext.Provider
      value={{
        timeNow,
        setTimeNow,
        allEvents,
        setAllEvents,
        selectedEvent,
        setSelectedEvent,
        completeList,
        setCompleteList,
        allCompanies,
        setAllCompanies,
        filteredList,
        setFilteredList,
        searchGuest,
        setSearchGuest,
        totalGuests,
        setTotalGuests,
        allGuests,
        setAllGuests,
      }}
    >
      {children}
    </InfoContext.Provider>
  );
};

export default InfoProvider;

export function useInfo() {
  const useInfo = useContext(InfoContext);
  return useInfo;
}
